JavaScript.Function and Global Variable Declaration:
let input = document.getElementById('pesquisar-help');

input.addEventListener('keydown', function(event) {
    if (event.key === 'Enter') {
        event.preventDefault(); 

        let valorInput = input.value.toLowerCase();

        let regioesPai = document.querySelectorAll('.t-Region');

        for (let regiaoPai of regioesPai) {
            let filhoVisivel = false;

            let regioesFilha = regiaoPai.getElementsByClassName('t-Region-titleButton');

            for (let i = 0; i < regioesFilha.length; i++) {
                let filho = regioesFilha[i];
                let conteudo = filho.closest('.row');

                if (conteudo) {
                    if (!filho.textContent.toLowerCase().includes(valorInput)) {
                        conteudo.style.display = "none";
                    } else {
                        conteudo.style.display = "flex";
                        filhoVisivel = true;
                    }
                }
            }

            if (!filhoVisivel) {
                regiaoPai.style.display = "none";
            } else {
                regiaoPai.style.display = "block";
            }
        }
    }
});

