// Definir a variável input antes do loop
let input = document.getElementById('pesquisar-help');

input.addEventListener('keydown', function(event) {
    if (event.key === 'Enter') {
        event.preventDefault(); // Evita o envio do formulário

        let inputValue = input.value.toLowerCase();

        // Selecionar todas as regiões .t-Region
        let regionParents = document.querySelectorAll('.t-Region');

        for (let regionParent of regionParents) {
            let anyRowVisible = false; // Variável para rastrear se alguma linha está visível

            let buttons = regionParent.getElementsByClassName('t-Region-titleButton');

            for (let i = 0; i < buttons.length; i++) {
                let button = buttons[i];
                let rowParent = button.closest('.row'); // Encontra o elemento pai com a classe 'row'

                if (rowParent) {
                    if (!button.textContent.toLowerCase().includes(inputValue)) {
                        rowParent.style.display = "none";
                    } else {
                        rowParent.style.display = "flex";
                        anyRowVisible = true; // Se pelo menos uma linha está visível
                    }
                }
            }

            if (!anyRowVisible) {
                regionParent.style.display = "none"; // Oculta a região pai se nenhuma linha está visível
            } else {
                regionParent.style.display = "block"; // Mostra a região pai se alguma linha está visível
            }
        }
    }
});

